<?php//// Конттроллер страницы чтения.//include_once('m/Text.php');include_once __DIR__."/../m/User.php";class C_Page extends C_Base{	//	// Конструктор.	//		public function action_index(){		$this->title .= '::Чтение';		$text = Text::text_get();        User::log($this->title);		$this->content = $this->Template('v/v_index.php', array('text' => $text));	}		public function action_edit(){		$this->title .= '::Редактирование';        User::log($this->title);		if($this->isPost())		{			Text::text_set($_POST['text']);			header('location: index.php');//			exit();		}				$text = Text::text_get();		$this->content = $this->Template('v/v_edit.php', array('text' => $text));			}}